/**
 * DeliveryOptimizer - Google Apps Script for managing and optimizing product deliveries
 * 
 * This script helps manage deliveries across multiple properties from various stores,
 * optimizing for minimal deliveries while respecting delivery capacity constraints.
 * 
 * Features:
 * - Analyzes property inventory needs
 * - Assigns orders to stores based on availability and delivery capacity
 * - Consolidates partial deliveries when possible
 * - Tracks and updates inventory
 * - Provides detailed reporting
 */

// Global constants
const MAX_DELIVERY_CAPACITY = 16; // Maximum stacks per delivery
const DELIVERY_FEE = 200; // Cost per delivery
const REQUIRED_SHEETS = ["Properties", "Items and Stores", "Order Optimization", "Summary"];
const PROPERTY_INFO = {
  "Bungalow": {stacksNeededCol: 5, currentInventoryCol: 3},
  "Barn": {stacksNeededCol: 9, currentInventoryCol: 8},
  "Docks Warehouse": {stacksNeededCol: 13, currentInventoryCol: 12}
};

/**
 * Main function to optimize deliveries across all properties
 */
function optimizeDeliveries() {
  const ss = SpreadsheetApp.getActiveSpreadsheet();

  // Validate sheets and get data
  try {
    const sheets = validateSheets(ss);
    const storeQueue = initializeStoreQueue();

    // Clear and reset output sheets
    sheets.optimizationSheet.clear();

    // Get data
    const propertiesData = getPropertiesData(sheets.propertiesSheet);
    const itemsData = getItemsAndStoresData(sheets.itemsAndStoresSheet);

    // Process each property
    const summaryData = processAllProperties(propertiesData, itemsData, sheets.optimizationSheet, storeQueue);

    // Format optimization sheet
    formatSheet(sheets.optimizationSheet);

    // Update summary sheet
    updateSummarySheet(summaryData, sheets.summarySheet);

    Logger.log("Delivery optimization completed successfully");
  } catch (error) {
    Logger.log(`Error: ${error.message}`);
    SpreadsheetApp.getUi().alert(`Error: ${error.message}`);
  }
}

/**
 * Validates that all required sheets exist
 * @param {SpreadsheetApp.Spreadsheet} ss - The active spreadsheet
 * @return {Object} Object containing sheet references
 */
function validateSheets(ss) {
  const sheets = {};
  const missingSheets = [];

  REQUIRED_SHEETS.forEach(sheetName => {
    const sheet = ss.getSheetByName(sheetName);
    if (sheet) {
      sheets[sheetName.toLowerCase().replace(/ /g, "") + "Sheet"] = sheet;
    } else {
      missingSheets.push(sheetName);
    }
  });

  if (missingSheets.length > 0) {
    throw new Error(`Missing sheets: ${missingSheets.join(", ")}`);
  }

  return sheets;
}

/**
 * Initialize or retrieve the store queue for fair distribution
 * @return {Array} Array of store names in queue order
 */
function initializeStoreQueue() {
  const stores = ["Dan's Hardware", "Handy Hank's", "Albert Hoover", "Shirley Watts", 
                  "Salvador Moreno", "Gas-Mart Central", "Gas-Mart West", "Oscar's Store"];

  let storeQueue = PropertiesService.getScriptProperties().getProperty("storeQueue");
  if (!storeQueue) {
    storeQueue = stores;
    PropertiesService.getScriptProperties().setProperty("storeQueue", JSON.stringify(storeQueue));
  } else {
    storeQueue = JSON.parse(storeQueue);

    // Ensure all stores are in the queue (in case stores were added)
    stores.forEach(store => {
      if (!storeQueue.includes(store)) {
        storeQueue.push(store);
      }
    });
  }

  Logger.log(`Store queue: ${storeQueue.join(', ')}`);
  return storeQueue;
}

/**
 * Retrieves and validates data from Properties sheet
 * @param {SpreadsheetApp.Sheet} propertiesSheet - The Properties sheet
 * @return {Array} Array of property data rows
 */
function getPropertiesData(propertiesSheet) {
  const lastRow = propertiesSheet.getLastRow();
  if (lastRow < 3) {
    throw new Error("Properties sheet must have data starting at row 3");
  }

  return propertiesSheet.getRange(3, 1, lastRow - 2, 13).getValues();
}

/**
 * Retrieves and validates data from Items and Stores sheet
 * @param {SpreadsheetApp.Sheet} itemsAndStoresSheet - The Items and Stores sheet
 * @return {Array} Array of item data rows
 */
function getItemsAndStoresData(itemsAndStoresSheet) {
  const lastRow = itemsAndStoresSheet.getLastRow();
  if (lastRow < 2) {
    throw new Error("Items and Stores sheet must have data starting at row 2");
  }

  return itemsAndStoresSheet.getRange(2, 1, lastRow - 1, 11).getValues();
}

/**
 * Process all properties to generate optimized deliveries
 * @param {Array} propertiesData - Data from Properties sheet
 * @param {Array} itemsData - Data from Items and Stores sheet
 * @param {SpreadsheetApp.Sheet} outputSheet - Sheet to write results
 * @param {Array} storeQueue - Store queue for fair distribution
 * @return {Array} Summary data for all processed deliveries
 */
function processAllProperties(propertiesData, itemsData, outputSheet, storeQueue) {
  const properties = Object.keys(PROPERTY_INFO);
  const stores = ["Dan's Hardware", "Handy Hank's", "Albert Hoover", "Shirley Watts", 
                 "Salvador Moreno", "Gas-Mart Central", "Gas-Mart West", "Oscar's Store"];

  let row = 1;
  let summaryData = [];

  // Process each property
  properties.forEach(property => {
    const propertyNeeds = identifyPropertyNeeds(property, propertiesData, itemsData);

    if (propertyNeeds.length === 0) {
      Logger.log(`No items needed for ${property}`);
      return;
    }

    // Create initial delivery plan
    const deliveryPlan = createDeliveryPlan(property, propertyNeeds, storeQueue);

    // Write delivery plan to sheet and collect summary data
    const propertySummary = writeDeliveryPlan(property, deliveryPlan, outputSheet, row);
    summaryData = summaryData.concat(propertySummary);

    // Update row position for next property
    row += propertySummary.reduce((total, entry) => {
      // Each store gets a header row + items rows + total row + 2 blank rows
      return total + entry[4] + 4;
    }, 0);

    Logger.log(`Processed ${property}: ${propertySummary.length} stores used`);
  });

  // Rotate store queue based on usage
  PropertiesService.getScriptProperties().setProperty("storeQueue", JSON.stringify(storeQueue));

  return summaryData;
}

/**
 * Identify items needed for a specific property
 * @param {string} property - The property name
 * @param {Array} propertiesData - Data from Properties sheet
 * @param {Array} itemsData - Data from Items and Stores sheet
 * @return {Array} Array of needed items with their details
 */
function identifyPropertyNeeds(property, propertiesData, itemsData) {
  const propertyInfo = PROPERTY_INFO[property];
  const stacksNeededCol = propertyInfo.stacksNeededCol - 1; // Convert to 0-based index
  const stores = ["Dan's Hardware", "Handy Hank's", "Albert Hoover", "Shirley Watts", 
                 "Salvador Moreno", "Gas-Mart Central", "Gas-Mart West", "Oscar's Store"];

  const propertyNeeds = [];

  propertiesData.forEach((row, index) => {
    const item = row[0];
    const stacksNeeded = row[stacksNeededCol];

    if (stacksNeeded > 0 && index < itemsData.length) {
      const itemInfo = itemsData[index];
      const stackSize = itemInfo[1];
      const costPerItem = itemInfo[2];

      if (stackSize <= 0 || costPerItem < 0) {
        Logger.log(`Warning: Invalid data for ${item}: stackSize=${stackSize}, costPerItem=${costPerItem}`);
        return;
      }

      // Find which stores carry this item
      const availableStores = [];
      stores.forEach((store, storeIndex) => {
        if (itemInfo[3 + storeIndex] === "Yes") {
          availableStores.push(store);
        }
      });

      if (availableStores.length > 0) {
        propertyNeeds.push({
          item,
          stacksNeeded,
          stackSize,
          costPerItem,
          availableStores,
          remainingStacks: stacksNeeded
        });

        Logger.log(`${property} needs ${stacksNeeded} stacks of ${item} (available at ${availableStores.length} stores)`);
      } else {
        Logger.log(`Warning: No stores available for ${item}`);
      }
    }
  });

  // Sort by availability (ascending) and quantity needed (descending)
  propertyNeeds.sort((a, b) => {
    if (a.availableStores.length !== b.availableStores.length) {
      return a.availableStores.length - b.availableStores.length;
    }
    return b.stacksNeeded - a.stacksNeeded;
  });

  return propertyNeeds;
}

/**
 * Create an optimized delivery plan for a property
 * @param {string} property - The property name
 * @param {Array} propertyNeeds - Items needed for the property
 * @param {Array} storeQueue - Store queue for fair distribution
 * @return {Object} Delivery plan organized by store
 */
function createDeliveryPlan(property, propertyNeeds, storeQueue) {
  const stores = [...new Set(propertyNeeds.flatMap(item => item.availableStores))];
  const deliveryPlan = {};

  // Initialize store delivery tracking
  stores.forEach(store => {
    deliveryPlan[store] = {
      deliveries: [],
      totalStacks: 0,
      totalItems: 0,
      totalCost: 0
    };
  });

  // First-pass: Assign items to their optimal stores
  assignItemsToStores(propertyNeeds, deliveryPlan, storeQueue);

  // Second-pass: Consolidate partial deliveries when possible
  consolidateDeliveries(propertyNeeds, deliveryPlan);

  // Final-pass: Calculate totals and finalize delivery plan
  finalizeDeliveryPlan(deliveryPlan);

  return deliveryPlan;
}

/**
 * Assign items to stores using an optimized algorithm
 * @param {Array} items - Items to be assigned
 * @param {Object} deliveryPlan - Current delivery plan
 * @param {Array} storeQueue - Store queue for fair distribution
 */
function assignItemsToStores(items, deliveryPlan, storeQueue) {
  // Clone items to track remaining needs
  let remainingItems = [...items];

  // First pass: Items with limited store availability
  remainingItems.sort((a, b) => a.availableStores.length - b.availableStores.length);

  while (remainingItems.some(item => item.remainingStacks > 0)) {
    // Sort store queue by usage
    const sortedStores = [...storeQueue].sort((a, b) => {
      const aDeliveries = deliveryPlan[a]?.deliveries.length || 0;
      const bDeliveries = deliveryPlan[b]?.deliveries.length || 0;
      return aDeliveries - bDeliveries;
    });

    let assigned = false;

    // Try to fill existing partial deliveries first
    for (const store of sortedStores) {
      if (!deliveryPlan[store]) continue;

      const deliveries = deliveryPlan[store].deliveries;
      if (deliveries.length === 0 || deliveries[deliveries.length - 1].stacks >= MAX_DELIVERY_CAPACITY) {
        continue; // No deliveries or last delivery is full
      }

      const currentDelivery = deliveries[deliveries.length - 1];

      // Try to add items to this partial delivery
      for (const item of remainingItems) {
        if (item.remainingStacks === 0 || !item.availableStores.includes(store)) {
          continue;
        }

        const spaceLeft = MAX_DELIVERY_CAPACITY - currentDelivery.stacks;
        const stacksToAdd = Math.min(item.remainingStacks, spaceLeft);

        if (stacksToAdd > 0) {
          // Add to delivery
          currentDelivery.items.push({
            item: item.item,
            stacksNeeded: stacksToAdd,
            stackSize: item.stackSize,
            costPerItem: item.costPerItem
          });

          currentDelivery.stacks += stacksToAdd;
          item.remainingStacks -= stacksToAdd;
          assigned = true;

          Logger.log(`Added ${stacksToAdd} stacks of ${item.item} to existing ${store} delivery (${currentDelivery.stacks}/${MAX_DELIVERY_CAPACITY})`);

          // Rotate store queue if delivery is now full
          if (currentDelivery.stacks >= MAX_DELIVERY_CAPACITY) {
            rotateStoreQueue(storeQueue, store);
            break;
          }
        }
      }
    }

    // Create new deliveries if needed
    if (!assigned) {
      // Find most constrained item (fewest store options)
      const targetItem = remainingItems.find(item => item.remainingStacks > 0);
      if (!targetItem) break;

      // Find best store for this item
      const bestStore = targetItem.availableStores.sort((a, b) => {
        // Prefer stores earlier in the queue
        return storeQueue.indexOf(a) - storeQueue.indexOf(b);
      })[0];

      if (!bestStore) {
        Logger.log(`Warning: No available store for ${targetItem.item}`);
        targetItem.remainingStacks = 0; // Mark as unassignable
        continue;
      }

      // Create new delivery
      const newDelivery = {
        items: [{
          item: targetItem.item,
          stacksNeeded: Math.min(targetItem.remainingStacks, MAX_DELIVERY_CAPACITY),
          stackSize: targetItem.stackSize,
          costPerItem: targetItem.costPerItem
        }],
        stacks: Math.min(targetItem.remainingStacks, MAX_DELIVERY_CAPACITY)
      };

      targetItem.remainingStacks -= newDelivery.items[0].stacksNeeded;
      deliveryPlan[bestStore].deliveries.push(newDelivery);

      Logger.log(`Created new delivery from ${bestStore} with ${newDelivery.items[0].stacksNeeded} stacks of ${targetItem.item}`);

      // Try to fill this new delivery with compatible items
      for (const item of remainingItems) {
        if (item === targetItem || item.remainingStacks === 0 || !item.availableStores.includes(bestStore)) {
          continue;
        }

        const spaceLeft = MAX_DELIVERY_CAPACITY - newDelivery.stacks;
        if (spaceLeft <= 0) break;

        const stacksToAdd = Math.min(item.remainingStacks, spaceLeft);

        if (stacksToAdd > 0) {
          newDelivery.items.push({
            item: item.item,
            stacksNeeded: stacksToAdd,
            stackSize: item.stackSize,
            costPerItem: item.costPerItem
          });

          newDelivery.stacks += stacksToAdd;
          item.remainingStacks -= stacksToAdd;

          Logger.log(`Added ${stacksToAdd} stacks of ${item.item} to new ${bestStore} delivery (${newDelivery.stacks}/${MAX_DELIVERY_CAPACITY})`);
        }
      }

      // Rotate store queue if delivery is full
      if (newDelivery.stacks >= MAX_DELIVERY_CAPACITY) {
        rotateStoreQueue(storeQueue, bestStore);
      }

      assigned = true;
    }

    // If no assignments were made, break to avoid infinite loop
    if (!assigned) {
      Logger.log("Warning: Could not assign remaining items");
      break;
    }

    // Remove fully assigned items
    remainingItems = remainingItems.filter(item => item.remainingStacks > 0);
  }
}

/**
 * Rotate a store to the end of the queue after a full delivery
 * @param {Array} storeQueue - Store queue to modify
 * @param {string} store - Store to rotate
 */
function rotateStoreQueue(storeQueue, store) {
  const index = storeQueue.indexOf(store);
  if (index !== -1) {
    storeQueue.splice(index, 1);
    storeQueue.push(store);
    Logger.log(`Rotated ${store} to end of queue: ${storeQueue.join(', ')}`);
  }
}

/**
 * Consolidate partial deliveries to minimize delivery count
 * @param {Array} items - All property items
 * @param {Object} deliveryPlan - Current delivery plan
 */
function consolidateDeliveries(items, deliveryPlan) {
  // Collect all partial deliveries
  let partialDeliveries = [];
  for (const store in deliveryPlan) {
    deliveryPlan[store].deliveries.forEach((delivery, index) => {
      if (delivery.stacks < MAX_DELIVERY_CAPACITY) {
        partialDeliveries.push({
          store,
          index,
          delivery,
          space: MAX_DELIVERY_CAPACITY - delivery.stacks
        });
      }
    });
  }

  // Sort by available space (ascending)
  partialDeliveries.sort((a, b) => a.space - b.space);

  // Try to merge pairs of partial deliveries
  let merged;
  do {
    merged = false;

    for (let i = 0; i < partialDeliveries.length; i++) {
      const d1 = partialDeliveries[i];

      for (let j = i + 1; j < partialDeliveries.length; j++) {
        const d2 = partialDeliveries[j];

        // Skip if same store or won't fit together
        if (d1.store === d2.store || d1.delivery.stacks + d2.delivery.stacks > MAX_DELIVERY_CAPACITY) {
          continue;
        }

        // Find common store for all items in both deliveries
        const allItems = [...d1.delivery.items, ...d2.delivery.items];
        const itemNames = allItems.map(i => i.item);

        // Get common stores for these items
        const commonStores = findCommonStores(itemNames, items);

        if (commonStores.length > 0) {
          // Take the first common store
          const targetStore = commonStores[0];

          // Create merged delivery
          const mergedDelivery = {
            items: [...allItems],
            stacks: d1.delivery.stacks + d2.delivery.stacks
          };

          // Remove original deliveries
          deliveryPlan[d1.store].deliveries.splice(d1.index, 1);

          // Adjust index if d2 is from same store and after d1
          if (d2.store === d1.store && d2.index > d1.index) {
            d2.index--;
          }

          deliveryPlan[d2.store].deliveries.splice(d2.index, 1);

          // Add merged delivery to target store
          if (!deliveryPlan[targetStore]) {
            deliveryPlan[targetStore] = {
              deliveries: [],
              totalStacks: 0,
              totalItems: 0,
              totalCost: 0
            };
          }

          deliveryPlan[targetStore].deliveries.push(mergedDelivery);

          Logger.log(`Merged deliveries from ${d1.store} and ${d2.store} into ${targetStore}`);

          // Rebuild partial deliveries list
          partialDeliveries = [];
          for (const store in deliveryPlan) {
            deliveryPlan[store].deliveries.forEach((delivery, index) => {
              if (delivery.stacks < MAX_DELIVERY_CAPACITY) {
                partialDeliveries.push({
                  store,
                  index,
                  delivery,
                  space: MAX_DELIVERY_CAPACITY - delivery.stacks
                });
              }
            });
          }

          partialDeliveries.sort((a, b) => a.space - b.space);
          merged = true;
          break;
        }
      }

      if (merged) break;
    }
  } while (merged);
}

/**
 * Find stores that can supply all the given items
 * @param {Array} itemNames - Names of items
 * @param {Array} allItems - Complete item data
 * @return {Array} Stores that can supply all items
 */
function findCommonStores(itemNames, allItems) {
  // Get all stores that carry each item
  const storesByItem = itemNames.map(itemName => {
    const itemData = allItems.find(i => i.item === itemName);
    return itemData ? itemData.availableStores : [];
  });

  // Find intersection of all store arrays
  if (storesByItem.length === 0) return [];

  return storesByItem.reduce((acc, stores) => {
    return acc.filter(store => stores.includes(store));
  }, storesByItem[0]);
}

/**
 * Calculate totals and finalize the delivery plan
 * @param {Object} deliveryPlan - Delivery plan to finalize
 */
function finalizeDeliveryPlan(deliveryPlan) {
  for (const store in deliveryPlan) {
    const plan = deliveryPlan[store];

    // Skip stores with no deliveries
    if (plan.deliveries.length === 0) continue;

    // Calculate totals
    plan.totalStacks = 0;
    plan.totalItems = 0;
    plan.totalCost = 0;

    plan.deliveries.forEach(delivery => {
      let deliveryItemCost = 0;

      delivery.items.forEach(item => {
        const itemsOrdered = item.stacksNeeded * item.stackSize;
        deliveryItemCost += itemsOrdered * item.costPerItem;
        plan.totalStacks += item.stacksNeeded;
        plan.totalItems += itemsOrdered;
      });

      // Add item cost plus delivery fee
      plan.totalCost += deliveryItemCost + DELIVERY_FEE;
    });

    Logger.log(`${store}: ${plan.deliveries.length} deliveries, ${plan.totalStacks} stacks, $${plan.totalCost}`);
  }
}

/**
 * Write the delivery plan to the output sheet
 * @param {string} property - The property name
 * @param {Object} deliveryPlan - Finalized delivery plan
 * @param {SpreadsheetApp.Sheet} sheet - Output sheet
 * @param {number} startRow - Starting row for output
 * @return {Array} Summary data for this property
 */
function writeDeliveryPlan(property, deliveryPlan, sheet, startRow) {
  let row = startRow;
  const summaryData = [];

  // Process each store with deliveries
  for (const store in deliveryPlan) {
    const plan = deliveryPlan[store];

    // Skip stores with no deliveries
    if (plan.deliveries.length === 0) continue;

    // Header
    sheet.getRange(row, 1).setValue(`${property} - ${store}`);
    row++;
    sheet.getRange(row, 1, 1, 5).setValues([["Item", "Stacks Needed", "Delivery Number", "Items Ordered", "Cost"]]);
    row++;

    // Write deliveries
    plan.deliveries.forEach((delivery, index) => {
      delivery.items.forEach(item => {
        const itemsOrdered = item.stacksNeeded * item.stackSize;
        const cost = itemsOrdered * item.costPerItem;

        sheet.getRange(row, 1, 1, 5).setValues([
          [item.item, item.stacksNeeded, index + 1, itemsOrdered, cost]
        ]);
        row++;
      });
    });

    // Total row
    sheet.getRange(row, 1, 1, 5).setValues([
      ["Total", plan.totalStacks, plan.deliveries.length, plan.totalItems, plan.totalCost]
    ]);
    row += 2; // Add space after each store

    // Add to summary data
    summaryData.push([
      property, 
      store, 
      plan.totalStacks, 
      plan.totalItems, 
      plan.deliveries.length, 
      plan.totalCost
    ]);
  }

  return summaryData;
}

/**
 * Update the summary sheet with delivery data
 * @param {Array} summaryData - Collected summary data
 * @param {SpreadsheetApp.Sheet} summarySheet - Summary sheet
 */
function updateSummarySheet(summaryData, summarySheet) {
  summarySheet.clear();

  if (summaryData.length === 0) {
    summarySheet.getRange(1, 1).setValue("No deliveries needed");
    return;
  }

  // Header
  summarySheet.getRange(1, 1, 1, 6).setValues([
    ["Property", "Store", "Total Stacks Ordered", "Total Items Ordered", "Total Deliveries", "Total Cost"]
  ]);

  // Data
  summarySheet.getRange(2, 1, summaryData.length, 6).setValues(summaryData);

  // Totals
  const totalStacks = summaryData.reduce((sum, row) => sum + row[2], 0);
  const totalItems = summaryData.reduce((sum, row) => sum + row[3], 0);
  const totalDeliveries = summaryData.reduce((sum, row) => sum + row[4], 0);
  const totalCost = summaryData.reduce((sum, row) => sum + row[5], 0);

  summarySheet.getRange(summaryData.length + 2, 1, 1, 6).setValues([
    ["Total", "", totalStacks, totalItems, totalDeliveries, totalCost]
  ]);

  // Format
  summarySheet.setFrozenRows(1);
  summarySheet.setFrozenColumns(2);

  Logger.log(`Summary updated: ${totalDeliveries} deliveries, ${totalStacks} stacks, $${totalCost}`);
}

/**
 * Format a sheet with basic styling
 * @param {SpreadsheetApp.Sheet} sheet - Sheet to format
 */
function formatSheet(sheet) {
  // Freeze headers
  sheet.setFrozenRows(1);
  sheet.setFrozenColumns(1);

  // Protect sheet
  try {
    const protection = sheet.protect().setDescription(`Protected ${sheet.getName()}`);
    protection.setWarningOnly(false);
    protection.removeEditors(protection.getEditors());
  } catch (e) {
    Logger.log(`Protection error: ${e.message}`);
  }
}

/**
 * Process and fulfill the next pending order
 */
function processNextOrder() {
  const ss = SpreadsheetApp.getActiveSpreadsheet();

  try {
    // Validate sheets
    const sheets = validateSheets(ss);
    const nextOrderSheet = ss.getSheetByName("Next Order");

    if (!nextOrderSheet) {
      throw new Error("Missing sheet: Next Order");
    }

    // Run optimization to refresh data
    optimizeDeliveries();

    // Clear and set up Next Order sheet
    nextOrderSheet.clear();
    nextOrderSheet.getRange(1, 1, 1, 7).setValues([["Item", "Stacks Needed", "Items Ordered", "Cost", "Property", "Store", "Status"]]);

    // Get optimization data
    const data = sheets.optimizationSheet.getRange(1, 1, sheets.optimizationSheet.getLastRow(), 5).getValues();

    // Process first delivery
    const firstDelivery = findFirstDelivery(data);

    if (firstDelivery.items.length === 0) {
      nextOrderSheet.getRange(2, 1).setValue("No pending orders");
      return;
    }

    // Write to Next Order sheet
    let row = 2;
    firstDelivery.items.forEach(item => {
      nextOrderSheet.getRange(row, 1, 1, 7).setValues([
        [item.item, item.stacksNeeded, item.itemsOrdered, item.cost, firstDelivery.property, firstDelivery.store, false]
      ]);
      row++;
    });

    // Update inventory
    updateInventory(firstDelivery);

    Logger.log(`Processed next order: ${firstDelivery.property} from ${firstDelivery.store}`);
  } catch (error) {
    Logger.log(`Error: ${error.message}`);
    SpreadsheetApp.getUi().alert(`Error: ${error.message}`);
  }
}

/**
 * Find the first delivery in the optimization data
 * @param {Array} data - Data from optimization sheet
 * @return {Object} First delivery information
 */
function findFirstDelivery(data) {
  let property = "", store = "";
  let items = [];
  let foundHeader = false;

  for (let i = 0; i < data.length; i++) {
    const row = data[i];

    // Check if this is a property-store header
    if (row[0] && row[0].includes(" - ")) {
      [property, store] = row[0].split(" - ");
      foundHeader = true;
      continue;
    }

    // Skip non-data rows
    if (!foundHeader || !row[0] || row[0] === "Item" || row[0] === "Total") {
      continue;
    }

    // Only collect items for delivery number 1
    if (row[2] === 1) {
      items.push({
        item: row[0],
        stacksNeeded: row[1],
        itemsOrdered: row[3],
        cost: row[4]
      });
    } else if (items.length > 0 && row[2] > 1) {
      // Once we see delivery number > 1, we've processed the first delivery
      break;
    }
  }

  return {
    property,
    store,
    items
  };
}

/**
 * Update inventory based on order fulfillment
 * @param {Object} delivery - Delivery information
 */
function updateInventory(delivery) {
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const propertiesSheet = ss.getSheetByName("Properties");

  if (!propertiesSheet) {
    Logger.log("Error: Properties sheet not found");
    return;
  }

  // Map properties to their inventory columns
  const propertyInventoryColumns = {
    "Bungalow": 3,         // Column C
    "Barn": 8,             // Column H
    "Docks Warehouse": 12  // Column L
  };

  const inventoryCol = propertyInventoryColumns[delivery.property];
  if (!inventoryCol) {
    Logger.log(`Error: Unknown property ${delivery.property}`);
    return;
  }

  // Get properties data
  const propertiesLastRow = propertiesSheet.getLastRow();
  const propertiesData = propertiesSheet.getRange(3, 1, propertiesLastRow - 2, 13).getValues();

  // Update inventory for each item
  let updatedItems = 0;

  delivery.items.forEach(item => {
    // Find the item in properties data
    for (let i = 0; i < propertiesData.length; i++) {
      if (propertiesData[i][0] === item.item) {
        // Get current inventory and update it
        const currentInventory = propertiesData[i][inventoryCol - 1] || 0;
        const newInventory = currentInventory + item.itemsOrdered;

        // Update the cell
        propertiesSheet.getRange(i + 3, inventoryCol).setValue(newInventory);

        Logger.log(`Updated ${delivery.property} inventory for ${item.item}: ${currentInventory} â†’ ${newInventory}`);
        updatedItems++;
        break;
      }
    }
  });

  if (updatedItems === 0) {
    Logger.log("Warning: No inventory was updated");
  } else {
    Logger.log(`Successfully updated inventory for ${updatedItems} items`);
  }
}

/**
 * Add menu items when the spreadsheet opens
