/**
 * Utility function to create a test environment
 * This can be useful for debugging or setting up a new spreadsheet
 */
function setupTestEnvironment() {
  const ss = SpreadsheetApp.getActiveSpreadsheet();

  // Check if sheets already exist
  const existingSheets = ss.getSheets().map(sheet => sheet.getName());
  const requiredSheets = ["Properties", "Items and Stores", "Order Optimization", "Summary", "Next Order"];

  // Create missing sheets
  requiredSheets.forEach(sheetName => {
    if (!existingSheets.includes(sheetName)) {
      const sheet = ss.insertSheet(sheetName);
      Logger.log(`Created sheet: ${sheetName}`);

      // Set up basic structure based on sheet type
      switch(sheetName) {
        case "Properties":
          setupPropertiesSheet(sheet);
          break;
        case "Items and Stores":
          setupItemsAndStoresSheet(sheet);
          break;
        default:
          // Other sheets will be populated by the script
          break;
      }
    }
  });

  Logger.log("Test environment setup complete");
}

/**
 * Setup the Properties sheet with sample data
 * @param {SpreadsheetApp.Sheet} sheet - The Properties sheet
 */
function setupPropertiesSheet(sheet) {
  // Headers
  sheet.getRange(1, 1).setValue("Properties Inventory and Order Management");

  const headers = [
    ["Item", "Bungalow", "", "", "", "Barn", "", "", "", "Docks Warehouse", "", "", ""],
    ["", "Current", "Min", "Max", "Stacks Needed", "Current", "Min", "Max", "Stacks Needed", "Current", "Min", "Max", "Stacks Needed"]
  ];

  sheet.getRange(1, 1, 2, 13).setValues(headers);

  // Sample data
  const sampleItems = [
    ["Lumber", 50, 25, 100, "=IF(B3<C3,D3-B3,0)", 30, 20, 80, "=IF(F3<G3,H3-F3,0)", 40, 30, 100, "=IF(J3<K3,L3-J3,0)"],
    ["Plywood", 15, 10, 40, "=IF(B4<C4,D4-B4,0)", 20, 15, 60, "=IF(F4<G4,H4-F4,0)", 25, 20, 80, "=IF(J4<K4,L4-J4,0)"],
    ["Nails", 200, 150, 500, "=IF(B5<C5,D5-B5,0)", 180, 150, 400, "=IF(F5<G5,H5-F5,0)", 220, 200, 600, "=IF(J5<K5,L5-J5,0)"],
    ["Screws", 180, 120, 400, "=IF(B6<C6,D6-B6,0)", 150, 100, 300, "=IF(F6<G6,H6-F6,0)", 200, 150, 450, "=IF(J6<K6,L6-J6,0)"]
  ];

  sheet.getRange(3, 1, sampleItems.length, 13).setValues(sampleItems);

  // Format
  sheet.setFrozenRows(2);
  sheet.setFrozenColumns(1);
}

/**
 * Setup the Items and Stores sheet with sample data
 * @param {SpreadsheetApp.Sheet} sheet - The Items and Stores sheet
 */
function setupItemsAndStoresSheet(sheet) {
  // Headers
  const storeNames = ["Dan's Hardware", "Handy Hank's", "Albert Hoover", "Shirley Watts", 
                     "Salvador Moreno", "Gas-Mart Central", "Gas-Mart West", "Oscar's Store"];

  const headerRow = ["Item", "Stack Size", "Cost per Item"].concat(storeNames);
  sheet.getRange(1, 1, 1, headerRow.length).setValues([headerRow]);

  // Sample data
  const sampleData = [
    ["Lumber", 25, 5, "Yes", "Yes", "No", "Yes", "No", "No", "Yes", "No"],
    ["Plywood", 10, 12, "Yes", "No", "Yes", "No", "Yes", "No", "No", "Yes"],
    ["Nails", 50, 0.5, "Yes", "Yes", "Yes", "Yes", "No", "Yes", "No", "No"],
    ["Screws", 40, 0.75, "No", "Yes", "Yes", "No", "Yes", "Yes", "Yes", "No"]
  ];

  sheet.getRange(2, 1, sampleData.length, headerRow.length).setValues(sampleData);

  // Format
  sheet.setFrozenRows(1);
  sheet.setFrozenColumns(3);
}

/**
 * Clear store queue and reset to default order
 * Useful for testing different scenarios
 */
function resetStoreQueue() {
  const stores = ["Dan's Hardware", "Handy Hank's", "Albert Hoover", "Shirley Watts", 
                 "Salvador Moreno", "Gas-Mart Central", "Gas-Mart West", "Oscar's Store"];

  PropertiesService.getScriptProperties().setProperty("storeQueue", JSON.stringify(stores));
  Logger.log(`Store queue reset to default: ${stores.join(', ')}`);
}

/**
 * Run a simulation with multiple delivery cycles
 * @param {number} cycles - Number of cycles to simulate (default: 5)
 */
function runDeliverySimulation(cycles = 5) {
  for (let i = 0; i < cycles; i++) {
    Logger.log(`===== Starting Simulation Cycle ${i + 1} =====`);

    // Optimize deliveries
    optimizeDeliveries();

    // Process next order
    processNextOrder();

    Logger.log(`===== Completed Simulation Cycle ${i + 1} =====`);

    // Small delay to avoid rate limits
    Utilities.sleep(1000);
  }

  Logger.log(`Simulation completed: ${cycles} cycles`);
}
